mapper.select:
select
  id, role_id, permission_id, status, created_at, updated_at
from t_role_permissions


        and id = #{id}


        and role_id = #{roleId}


        and permission_id = #{permissionId}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_role_permissions


        and id = #{id}


        and role_id = #{roleId}


        and permission_id = #{permissionId}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
