mapper.select:
select
  id, name, description, status, created_at, updated_at
from t_roles


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_roles


        and id = #{id}


        and name = #{name}


        and description = #{description}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
