mapper.select:
select
  id, user_id, role_id, status, created_at, updated_at
from t_user_roles


        and id = #{id}


        and user_id = #{userId}


        and role_id = #{roleId}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_user_roles


        and id = #{id}


        and user_id = #{userId}


        and role_id = #{roleId}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
