mapper.select:
select
  id, username, password, email, status, created_at, updated_at
from t_users


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_users


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and email = #{email}


        and status = #{status}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}
